pragma solidity 0.4.24;

// Internal
import "./BetProxy.sol";
import "./BetPayments.sol";
// External
import "openzeppelin-solidity/contracts/lifecycle/Pausable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";


/**
 * Stores the bets of the platform and provides a way to verify the integrity
 * of the tokens.
 *
 * Author: Carlos Gonzalez -- Github: carlosgj94
 */
/** @title Bet BetRegistry. */
contract BetRegistry is Pausable {
    using SafeMath for uint;

    // Events
    event LogEventEntry(
        bytes32 betEntryHash
    );

    // Storage
    struct BetEntry {
        address proxyContract;
        address oracleContract;
        address paymentContrac;
        address termsContract;
        bytes32 termsHash;
        address creator;
    }

    // Primary registry mapping the events with their hash
    mapping(bytes32 => BetEntry) internal betRegistry;

    /**
     * @dev Function to create bets in the registry
     * @param _kernelContract address Address of the better
     * @param _oracleContract address Address of the better
     * @param _paymentContract address Address of the better
     * @param _termsContract address Address of the better
     * @param _termsHash bytes32 Address of the better
     * @return bytes32 the that identifies the bet
     */
    function createBet(
        address _kernelContract,
        address _oracleContract,
        address _paymentContract,
        address _termsContract,
        bytes32 _termsHash,
        uint _salt
    ) public returns(bytes32) {
        BetEntry memory _entry = BetEntry(
            _kernelContract,
            _oracleContract,
            _paymentContract,
            _termsContract,
            _termsHash,
            msg.sender
        );

        bytes32 _hash = keccak256(
            abi.encodePacked(
                _kernelContract,
                _oracleContract,
                _paymentContract,
                _termsContract,
                _termsHash,
                msg.sender,
                _salt
            )
        );

        betRegistry[_hash] = _entry;

        emit LogEventEntry(_hash);

        return _hash;
    }
}

